// Write a program to calculate area of Square, Rectangle and Circle.
using System;class GeometryCalculator{    public static void Main(String[] args)    {        Console.WriteLine("Enter the side of the square:");        double Side = Convert.ToDouble(Console.ReadLine());        double Area = Side * Side;        Console.WriteLine("Area of the square: " + Area);        Console.WriteLine("Enter the length of the rectangle:");        double Length = Convert.ToDouble(Console.ReadLine());        Console.WriteLine("Enter the width of the rectangle:");        double Width = Convert.ToDouble(Console.ReadLine());        double rectangleArea = Length * Width;        Console.WriteLine("Area of the rectangle: " + rectangleArea);        Console.WriteLine("Enter the radius of the circle:");        double Radius = Convert.ToDouble(Console.ReadLine());        double circleArea = Math.PI * Radius * Radius;        Console.WriteLine("Area of the circle: " + circleArea);    }}